import React, { useState, useEffect } from 'react';
import { 
  Box, 
  Typography, 
  Paper, 
  Button, 
  TextField, 
  InputAdornment,
  Table, 
  TableBody, 
  TableCell, 
  TableContainer, 
  TableHead, 
  TableRow,
  TablePagination,
  IconButton,
  Tooltip,
  CircularProgress,
  Chip
} from '@mui/material';
import { styled } from '@mui/material/styles';
import AddIcon from '@mui/icons-material/Add';
import SearchIcon from '@mui/icons-material/Search';
import EditIcon from '@mui/icons-material/Edit';
import DeleteIcon from '@mui/icons-material/Delete';
import VisibilityIcon from '@mui/icons-material/Visibility';
import DescriptionIcon from '@mui/icons-material/Description';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

// API Service für Angebote
const API_URL = 'http://localhost:3000/api';

// Styled-Komponente für den Header-Bereich
const HeaderBox = styled(Box)(({ theme }) => ({
  display: 'flex',
  justifyContent: 'space-between',
  alignItems: 'center',
  marginBottom: theme.spacing(3),
}));

// Styled-Komponente für die Suchleiste
const SearchBox = styled(Box)(({ theme }) => ({
  display: 'flex',
  alignItems: 'center',
  marginBottom: theme.spacing(3),
}));

// Status-Chip-Komponente
const StatusChip = ({ status }) => {
  let color = 'default';
  let label = status;

  switch (status) {
    case 'created':
      color = 'info';
      label = 'Erstellt';
      break;
    case 'sent':
      color = 'primary';
      label = 'Gesendet';
      break;
    case 'accepted':
      color = 'success';
      label = 'Angenommen';
      break;
    case 'rejected':
      color = 'error';
      label = 'Abgelehnt';
      break;
    default:
      color = 'default';
      label = status;
  }

  return <Chip label={label} color={color} size="small" />;
};

const QuoteList = () => {
  const navigate = useNavigate();
  const [quotes, setQuotes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [page, setPage] = useState(0);
  const [rowsPerPage, setRowsPerPage] = useState(10);
  const [searchTerm, setSearchTerm] = useState('');

  // Angebote von der API abrufen
  useEffect(() => {
    const fetchQuotes = async () => {
      try {
        setLoading(true);
        // In einer vollständigen Implementierung würde hier ein API-Endpunkt für alle Angebote verwendet werden
        // Da wir diesen noch nicht implementiert haben, simulieren wir die Daten
        // const response = await axios.get(`${API_URL}/quotes`);

        // Simulierte Daten für die Demonstration
        const simulatedQuotes = [
          {
            quote_id: 1,
            account_id: 1,
            property_id: 1,
            contact_id: 1,
            quote_number: 'ANG-2025-001',
            quote_date: '2025-04-01',
            valid_until: '2025-05-01',
            status: 'sent',
            total_net: 850.00,
            total_gross: 1011.50,
            notes: 'Angebot für jährliche Wartung der Heizungsanlage',
            account_name: 'Hausverwaltung Schmidt GmbH',
            property_name: 'Wohnanlage Mitte',
            contact_name: 'Thomas Schmidt'
          },
          {
            quote_id: 2,
            account_id: 2,
            property_id: 3,
            contact_id: 3,
            quote_number: 'ANG-2025-002',
            quote_date: '2025-04-02',
            valid_until: '2025-05-02',
            status: 'created',
            total_net: 350.00,
            total_gross: 416.50,
            notes: 'Angebot für monatliche Reinigung der Treppenhäuser',
            account_name: 'Immobilien Müller & Co.',
            property_name: 'Wohnkomplex Süd',
            contact_name: 'Julia Fischer'
          },
          {
            quote_id: 3,
            account_id: 3,
            property_id: 4,
            contact_id: 4,
            quote_number: 'ANG-2025-003',
            quote_date: '2025-04-03',
            valid_until: '2025-05-03',
            status: 'accepted',
            total_net: 120.00,
            total_gross: 142.80,
            notes: 'Angebot für monatliche Gartenpflege',
            account_name: 'Hausverwaltung Becker',
            property_name: 'Geschäftshaus Zentrum',
            contact_name: 'Peter Becker'
          },
          {
            quote_id: 4,
            account_id: 1,
            property_id: 2,
            contact_id: 2,
            quote_number: 'ANG-2025-004',
            quote_date: '2025-04-04',
            valid_until: '2025-05-04',
            status: 'rejected',
            total_net: 95.00,
            total_gross: 113.05,
            notes: 'Angebot für Reparatur des Aufzugs',
            account_name: 'Hausverwaltung Schmidt GmbH',
            property_name: 'Bürogebäude Kreuzberg',
            contact_name: 'Anna Müller'
          }
        ];

        setQuotes(simulatedQuotes);
        setLoading(false);
      } catch (err) {
        console.error('Fehler beim Abrufen der Angebote:', err);
        setError('Fehler beim Laden der Angebote');
        setLoading(false);
      }
    };

    fetchQuotes();
  }, []);

  // Seitenänderung
  const handleChangePage = (event, newPage) => {
    setPage(newPage);
  };

  // Anzahl der Zeilen pro Seite ändern
  const handleChangeRowsPerPage = (event) => {
    setRowsPerPage(parseInt(event.target.value, 10));
    setPage(0);
  };

  // Zur Angebot-Detail-Seite navigieren
  const handleViewQuote = (id) => {
    navigate(`/quotes/${id}`);
  };

  // Zur Angebot-Bearbeiten-Seite navigieren
  const handleEditQuote = (id) => {
    navigate(`/quotes/edit/${id}`);
  };

  // Angebot löschen
  const handleDeleteQuote = async (id) => {
    if (window.confirm('Sind Sie sicher, dass Sie dieses Angebot löschen möchten?')) {
      try {
        // In einer vollständigen Implementierung würde hier ein API-Aufruf erfolgen
        // const response = await axios.delete(`${API_URL}/quotes/${id}`);

        // Simulierte Löschung für die Demonstration
        setQuotes(quotes.filter(quote => quote.quote_id !== id));
      } catch (err) {
        console.error('Fehler beim Löschen des Angebots:', err);
        alert('Fehler beim Löschen des Angebots');
      }
    }
  };

  // Neues Angebot erstellen
  const handleCreateQuote = () => {
    navigate('/quotes/new');
  };

  // Suche
  const handleSearch = (event) => {
    setSearchTerm(event.target.value);
    setPage(0);
  };

  // Zum Account navigieren
  const handleNavigateToAccount = (accountId) => {
    navigate(`/accounts/${accountId}`);
  };

  // Angebote filtern
  const filteredQuotes = quotes.filter(quote => 
    quote.quote_number.toLowerCase().includes(searchTerm.toLowerCase()) ||
    quote.account_name.toLowerCase().includes(searchTerm.toLowerCase()) ||
    quote.property_name.toLowerCase().includes(searchTerm.toLowerCase()) ||
    quote.contact_name.toLowerCase().includes(searchTerm.toLowerCase()) ||
    (quote.notes && quote.notes.toLowerCase().includes(searchTerm.toLowerCase()))
  );

  // Paginierte Angebote
  const paginatedQuotes = filteredQuotes.slice(
    page * rowsPerPage,
    page * rowsPerPage + rowsPerPage
  );

  // Formatieren von Währungsbeträgen
  const formatCurrency = (amount) => {
    return new Intl.NumberFormat('de-DE', { style: 'currency', currency: 'EUR' }).format(amount);
  };

  // Formatieren von Datumsangaben
  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('de-DE');
  };

  return (
    <Box>
      <HeaderBox>
        <Typography variant="h4">
          Angebote
        </Typography>
        <Button 
          variant="contained" 
          color="primary" 
          startIcon={<AddIcon />}
          onClick={handleCreateQuote}
        >
          Neues Angebot
        </Button>
      </HeaderBox>

      <SearchBox>
        <TextField
          fullWidth
          variant="outlined"
          placeholder="Suche nach Angebotsnummer, Account, Hausobjekt oder Kontakt"
          value={searchTerm}
          onChange={handleSearch}
          InputProps={{
            startAdornment: (
              <InputAdornment position="start">
                <SearchIcon />
              </InputAdornment>
            ),
          }}
        />
      </SearchBox>

      <Paper elevation={2}>
        {loading ? (
          <Box sx={{ display: 'flex', justifyContent: 'center', p: 3 }}>
            <CircularProgress />
          </Box>
        ) : error ? (
          <Box sx={{ p: 3, textAlign: 'center' }}>
            <Typography color="error">{error}</Typography>
          </Box>
        ) : (
          <>
            <TableContainer>
              <Table>
                <TableHead>
                  <TableRow>
                    <TableCell><strong>Angebotsnummer</strong></TableCell>
                    <TableCell><strong>Datum</strong></TableCell>
                    <TableCell><strong>Account</strong></TableCell>
                    <TableCell><strong>Betrag</strong></TableCell>
                    <TableCell><strong>Status</strong></TableCell>
                    <TableCell align="right"><strong>Aktionen</strong></TableCell>
                  </TableRow>
                </TableHead>
                <TableBody>
                  {paginatedQuotes.length > 0 ? (
                    paginatedQuotes.map((quote) => (
                      <TableRow key={quote.quote_id}>
                        <TableCell>
                          <Box sx={{ display: 'flex', alignItems: 'center' }}>
                            <DescriptionIcon sx={{ mr: 1, color: 'primary.main' }} />
                            {quote.quote_number}
                          </Box>
                        </TableCell>
                        <TableCell>{formatDate(quote.quote_date)}</TableCell>
                        <TableCell>
                          <Chip 
                            label={quote.account_name} 
                            color="primary" 
                            variant="outlined" 
                            clickable
                            onClick={() => handleNavigateToAccount(quote.account_id)}
                            size="small"
                          />
                        </TableCell>
                        <TableCell>{formatCurrency(quote.total_gross)}</TableCell>
                        <TableCell>
                          <StatusChip status={quote.status} />
                        </TableCell>
                        <TableCell align="right">
                          <Tooltip title="Details anzeigen">
                            <IconButton 
                              color="primary"
                              onClick={() => handleViewQuote(quote.quote_id)}
                            >
                              <VisibilityIcon />
                            </IconButton>
                          </Tooltip>
                          <Tooltip title="Bearbeiten">
                            <IconButton 
                              color="primary"
                              onClick={() => handleEditQuote(quote.quote_id)}
                            >
                              <EditIcon />
                            </IconButton>
                          </Tooltip>
                          <Tooltip title="Löschen">
                            <IconButton 
                              color="error"
                              onClick={() => handleDeleteQuote(quote.quote_id)}
                            >
                              <DeleteIcon />
                            </IconButton>
                          </Tooltip>
                        </TableCell>
                      </TableRow>
                    ))
                  ) : (
                    <TableRow>
                      <TableCell colSpan={6} align="center">
                        Keine Angebote gefunden
                      </TableCell>
                    </TableRow>
                  )}
                </TableBody>
              </Table>
            </TableContainer>
            <TablePagination
              rowsPerPageOptions={[5, 10, 25]}
              component="div"
              count={filteredQuotes.length}
              rowsPerPage={rowsPerPage}
              page={page}
              onPageChange={handleChangePage}
              onRowsPerPageChange={handleChangeRowsPerPage}
              labelRowsPerPage="Zeilen pro Seite:"
              labelDisplayedRows={({ from, to, count }) => `${from}-${to} von ${count}`}
            />
          </>
        )}
      </Paper>
    </Box>
  );
};

export default QuoteList;
