import React, { useState, useEffect } from 'react';
import { 
  Box, 
  Typography, 
  Paper, 
  Button, 
  Grid,
  Card,
  CardContent,
  CardHeader,
  Divider,
  Tabs,
  Tab,
  List,
  ListItem,
  ListItemText,
  ListItemIcon,
  ListItemSecondaryAction,
  IconButton,
  Tooltip,
  CircularProgress
} from '@mui/material';
import { styled } from '@mui/material/styles';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import EditIcon from '@mui/icons-material/Edit';
import DeleteIcon from '@mui/icons-material/Delete';
import ArrowBackIcon from '@mui/icons-material/ArrowBack';
import ContactsIcon from '@mui/icons-material/Contacts';
import HomeWorkIcon from '@mui/icons-material/HomeWork';
import DescriptionIcon from '@mui/icons-material/Description';
import ReceiptIcon from '@mui/icons-material/Receipt';
import EmailIcon from '@mui/icons-material/Email';
import PhoneIcon from '@mui/icons-material/Phone';
import LanguageIcon from '@mui/icons-material/Language';
import BusinessIcon from '@mui/icons-material/Business';
import ReceiptLongIcon from '@mui/icons-material/ReceiptLong';

// API Service für Accounts
const API_URL = 'http://localhost:3000/api';

// Styled-Komponente für den Header-Bereich
const HeaderBox = styled(Box)(({ theme }) => ({
  display: 'flex',
  justifyContent: 'space-between',
  alignItems: 'center',
  marginBottom: theme.spacing(3),
}));

// Styled-Komponente für die Info-Karten
const InfoCard = styled(Card)(({ theme }) => ({
  height: '100%',
  display: 'flex',
  flexDirection: 'column',
}));

// TabPanel-Komponente für die Tabs
function TabPanel(props) {
  const { children, value, index, ...other } = props;

  return (
    <div
      role="tabpanel"
      hidden={value !== index}
      id={`account-tabpanel-${index}`}
      aria-labelledby={`account-tab-${index}`}
      {...other}
    >
      {value === index && (
        <Box sx={{ p: 3 }}>
          {children}
        </Box>
      )}
    </div>
  );
}

const AccountDetail = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [account, setAccount] = useState(null);
  const [contacts, setContacts] = useState([]);
  const [properties, setProperties] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [tabValue, setTabValue] = useState(0);

  // Account und zugehörige Daten von der API abrufen
  useEffect(() => {
    const fetchAccountData = async () => {
      try {
        setLoading(true);

        // Account-Daten abrufen
        const accountResponse = await axios.get(`${API_URL}/accounts/${id}`);
        if (accountResponse.data.success) {
          setAccount(accountResponse.data.data);
        } else {
          setError('Fehler beim Laden des Accounts');
          return;
        }

        // Kontakte des Accounts abrufen
        const contactsResponse = await axios.get(`${API_URL}/accounts/${id}/contacts`);
        if (contactsResponse.data.success) {
          setContacts(contactsResponse.data.data);
        }

        // Hausobjekte des Accounts abrufen
        const propertiesResponse = await axios.get(`${API_URL}/accounts/${id}/properties`);
        if (propertiesResponse.data.success) {
          setProperties(propertiesResponse.data.data);
        }
      } catch (err) {
        console.error('Fehler beim Abrufen der Account-Daten:', err);
        setError('Fehler beim Laden der Account-Daten');
      } finally {
        setLoading(false);
      }
    };

    fetchAccountData();
  }, [id]);

  // Tab-Wechsel
  const handleTabChange = (event, newValue) => {
    setTabValue(newValue);
  };

  // Zurück zur Account-Liste
  const handleBack = () => {
    navigate('/accounts');
  };

  // Account bearbeiten
  const handleEdit = () => {
    navigate(`/accounts/${id}/edit`);
  };

  // Account löschen
  const handleDelete = async () => {
    if (window.confirm('Sind Sie sicher, dass Sie diesen Account löschen möchten?')) {
      try {
        const response = await axios.delete(`${API_URL}/accounts/${id}`);
        if (response.data.success) {
          navigate('/accounts');
        } else {
          alert('Fehler beim Löschen des Accounts');
        }
      } catch (err) {
        console.error('Fehler beim Löschen des Accounts:', err);
        alert('Fehler beim Löschen des Accounts');
      }
    }
  };

  // Kontakt anzeigen
  const handleViewContact = (contactId) => {
    navigate(`/contacts/${contactId}`);
  };

  // Hausobjekt anzeigen
  const handleViewProperty = (propertyId) => {
    navigate(`/properties/${propertyId}`);
  };

  if (loading) {
    return (
      <Box sx={{ display: 'flex', justifyContent: 'center', p: 3 }}>
        <CircularProgress />
      </Box>
    );
  }

  if (error || !account) {
    return (
      <Box>
        <Button 
          startIcon={<ArrowBackIcon />} 
          onClick={handleBack}
          sx={{ mb: 3 }}
        >
          Zurück zur Liste
        </Button>
        <Paper sx={{ p: 3, textAlign: 'center' }}>
          <Typography color="error">
            {error || 'Account nicht gefunden'}
          </Typography>
        </Paper>
      </Box>
    );
  }

  return (
    <Box>
      <HeaderBox>
        <Box sx={{ display: 'flex', alignItems: 'center' }}>
          <Button 
            startIcon={<ArrowBackIcon />} 
            onClick={handleBack}
            sx={{ mr: 2 }}
          >
            Zurück
          </Button>
          <Typography variant="h4">
            {account.name}
          </Typography>
        </Box>
        <Box>
          <Button 
            variant="outlined" 
            color="primary" 
            startIcon={<EditIcon />}
            onClick={handleEdit}
            sx={{ mr: 1 }}
          >
            Bearbeiten
          </Button>
          <Button 
            variant="outlined" 
            color="error" 
            startIcon={<DeleteIcon />}
            onClick={handleDelete}
          >
            Löschen
          </Button>
        </Box>
      </HeaderBox>

      <Grid container spacing={3} sx={{ mb: 3 }}>
        <Grid item xs={12} md={6}>
          <InfoCard>
            <CardHeader title="Kontaktinformationen" />
            <Divider />
            <CardContent>
              <List>
                <ListItem>
                  <ListItemIcon>
                    <BusinessIcon />
                  </ListItemIcon>
                  <ListItemText 
                    primary="Adresse" 
                    secondary={account.address || 'Keine Adresse angegeben'} 
                  />
                </ListItem>
                <ListItem>
                  <ListItemIcon>
                    <PhoneIcon />
                  </ListItemIcon>
                  <ListItemText 
                    primary="Telefon" 
                    secondary={account.phone || 'Keine Telefonnummer angegeben'} 
                  />
                </ListItem>
                <ListItem>
                  <ListItemIcon>
                    <EmailIcon />
                  </ListItemIcon>
                  <ListItemText 
                    primary="E-Mail" 
                    secondary={account.email || 'Keine E-Mail-Adresse angegeben'} 
                  />
                </ListItem>
                <ListItem>
                  <ListItemIcon>
                    <LanguageIcon />
                  </ListItemIcon>
                  <ListItemText 
                    primary="Website" 
                    secondary={account.website || 'Keine Website angegeben'} 
                  />
                </ListItem>
              </List>
            </CardContent>
          </InfoCard>
        </Grid>
        <Grid item xs={12} md={6}>
          <InfoCard>
            <CardHeader title="Zusätzliche Informationen" />
            <Divider />
            <CardContent>
              <List>
                <ListItem>
                  <ListItemIcon>
                    <ReceiptLongIcon />
                  </ListItemIcon>
                  <ListItemText 
                    primary="Steuernummer" 
                    secondary={account.tax_number || 'Keine Steuernummer angegeben'} 
                  />
                </ListItem>
                <ListItem>
                  <ListItemText 
                    primary="Notizen" 
                    secondary={account.notes || 'Keine Notizen vorhanden'} 
                  />
                </ListItem>
              </List>
            </CardContent>
          </InfoCard>
        </Grid>
      </Grid>

      <Paper sx={{ width: '100%' }}>
        <Tabs
          value={tabValue}
          onChange={handleTabChange}
          indicatorColor="primary"
          textColor="primary"
          variant="fullWidth"
        >
          <Tab icon={<ContactsIcon />} label="Kontakte" />
          <Tab icon={<HomeWorkIcon />} label="Hausobjekte" />
          <Tab icon={<DescriptionIcon />} label="Angebote" />
          <Tab icon={<ReceiptIcon />} label="Rechnungen" />
        </Tabs>

        <TabPanel value={tabValue} index={0}>
          {contacts.length > 0 ? (
            <List>
              {contacts.map((contact) => (
                <ListItem key={contact.contact_id} button onClick={() => handleViewContact(contact.contact_id)}>
                  <ListItemIcon>
                    <ContactsIcon />
                  </ListItemIcon>
                  <ListItemText 
                    primary={`${contact.first_name} ${contact.last_name}`} 
                    secondary={contact.position || contact.email || ''} 
                  />
                  <ListItemSecondaryAction>
                    <Tooltip title="Details anzeigen">
                      <IconButton edge="end" onClick={() => handleViewContact(contact.contact_id)}>
                        <EditIcon />
                      </IconButton>
                    </Tooltip>
                  </ListItemSecondaryAction>
                </ListItem>
              ))}
            </List>
          ) : (
            <Typography align="center" sx={{ py: 3 }}>
              Keine Kontakte vorhanden
            </Typography>
          )}
        </TabPanel>

        <TabPanel value={tabValue} index={1}>
          {properties.length > 0 ? (
            <List>
              {properties.map((property) => (
                <ListItem key={property.property_id} button onClick={() => handleViewProperty(property.property_id)}>
                  <ListItemIcon>
                    <HomeWorkIcon />
                  </ListItemIcon>
                  <ListItemText 
                    primary={property.name} 
                    secondary={`${property.street} ${property.house_number}, ${property.postal_code} ${property.city}`} 
                  />
                  <ListItemSecondaryAction>
                    <Tooltip title="Details anzeigen">
                      <IconButton edge="end" onClick={() => handleViewProperty(property.property_id)}>
                        <EditIcon />
                      </IconButton>
                    </Tooltip>
                  </ListItemSecondaryAction>
                </ListItem>
              ))}
            </List>
          ) : (
            <Typography align="center" sx={{ py: 3 }}>
              Keine Hausobjekte vorhanden
            </Typography>
          )}
        </TabPanel>

        <TabPanel value={tabValue} index={2}>
          <Typography align="center" sx={{ py: 3 }}>
            Keine Angebote vorhanden
          </Typography>
        </TabPanel>

        <TabPanel value={tabValue} index={3}>
          <Typography align="center" sx={{ py: 3 }}>
            Keine Rechnungen vorhanden
          </Typography>
        </TabPanel>
      </Paper>
    </Box>
  );
};

export default AccountDetail;
